import Sidebar from '../../components/sidebar/Sidebar'
import './settings.css'
import { useContext, useState } from "react";
import { Context } from "../../context/Context";
import axios from "axios";

export default function Settings() {
  const [file, setFile] = useState(null);
  const [username, setUsername] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [success, setSucess] = useState(false)

  const client = axios.create({
    baseURL: "http://localhost:5000/api/"
  })

  const { user, dispatch } = useContext(Context)

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch({type: "UPDATE_START"})
    const updatedUser = {
      username,
      email,
      password,
    }
    if (file) {
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file)
      updatedUser.profilePic = filename;
      try {
        await client.post("/upload", data);
      } catch (error) {}
    }
    try {
      const res = await client.put("/users/" + user._id, updatedUser);
      setSucess(true);
      dispatch({type: "UPDATE_SUCCESS", payload: res.date})
    } catch (error) {
      dispatch({type: "UPDATE_FAILURE"})
    } 
  }
  return (
    <div className='settings'>
      <div className="settingsWrapper">
        <div className="settingsTitle">
          <span className="settingsUpdateTitle">Update Your Account</span>
          <span className="settingsDeleteTitle">Delete Account</span>
        </div>
        <form className="settingsForm" onSubmit={handleSubmit}>
          <label>Profile Picture</label>
          <div className="settingsPP">
            <img 
            src={file ? URL.createObjectURL(file) : user.profilePic}
            alt="profile pix" 
            />
            <label htmlFor='fileInput'>
              <i className='settingsPPIcon far fa-user-circle'></i>
            </label>
            <input type="file" id='fileInput' style={{display:"none"}} 
            onChange={(e) => setFile(e.target.file[0])}
            />
          </div>
          <label>Username</label>
          <input type="text" placeholder={user.username}
          onChange={(e) => setUsername(e.target.value)} 
          />
          <label>Email</label>
          <input type="email" placeholder={user.email}
          onChange={(e) => setEmail(e.target.value)} 
          />
          <label>Password</label>
          <input type="password" 
          onChange={(e) => setPassword(e.target.value)}
          />
          <button className="settingsSubmit" type="submit">Update</button>
          { success && (<span style={{color: "green", textAlign: "center", marginTop: 20}}>Profile has been updated...</span>
          )}
        </form>
      </div>
      <Sidebar/>
    </div>
  )
}
