import './SinglePost.css'
import { useLocation } from "react-router"
import { useContext, useEffect, useState } from 'react';
import axios from "axios"
import { Link } from 'react-router-dom';
import { Context } from '../../context/Context';

export default function SinglePost() {
  const location = useLocation()
  const path = location.pathname.split("/")[2];
  const [post, setPost] = useState({})
  const PF = "http://localhost:5000/images/";
  const { user } = useContext(Context);
  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("")
  const [updateMode, setUpdateMode] = useState(false)

  const client = axios.create({
    baseURL: "http://localhost:5000/api/"
  })

  useEffect(() => {
    const getPost = async () => {
      const res = await client.get("/posts/" + path);
      setPost(res.data)
    }
    getPost();
  }, [client, path]);

  const handleDelete = async () => {
    try{
      await client.delete(`/posts/${post._id}`, {data: {username: user.username}});
      window.location.replace("/");
    } catch(error) {}
  }
  return (
    <div className='singlePost'>
      <div className="singlePostWrapper">
        {post.photo && (
        <img 
        src={ PF + post.photo} 
        alt="" 
        className='singlePostImg' 
        />
        )}
        { updateMode ? <input type="text" value={post.title } className="singlePostTitleInput" autoFocus /> : (
            <h1 className="singlePostTitle">
        {post.title}
        {post.username === user?.username && (
        <div className="singlePostEdit">
            <i className="singlePostIcon" class="far fa-edit" onClick={() => setUpdateMode(true)}></i>
            <i className="singlePostIcon" class="far fa-trash-alt" onClick={handleDelete}></i>
        </div>
        )}
        </h1>
        )}

        <div className="singlePostInfo">
            <span className='singlePostAuthor'>
                Author: 
                <Link to={`/?user=${post.username}`} className='link'>
                <b>{post.username}</b>
                </Link>
                </span>
            <span className='singlePostDate'>{new Date(post.createdAt).toDateString()}</span>
        </div>
        {updateMode ? (
          <textarea className='singlePostDescInput' />
        ) : (
          <p className='singlePostDesc'>{post.desc}</p>
        )}

      </div> 
    </div>
  )
}
